import React, { useRef } from "react";

function App() {
  const play = () => {
    console.log("play");
  };
  const pause = () => {
    console.log(videoState.current);
  };

  const videoState = useRef("play");

  const src = "https://media.w3.org/2010/05/sintel/trailer_hd.mp4 ";
  return (
    <div>
      <video
        controls
        width="100%"
        src={src}
        type="video/mp4"
        // preload={videoState}
        autoPlay
      />
      <button onClick={play} id="play">
        play
      </button>
      <button onClick={pause} id="pause">
        pause
      </button>
    </div>
  );
}

export default App;
