import Child from "./Child";

const Mother = (props) => {
  const [btnTitle, setBtnTitle] = useState("click me");

  //   let title = "click me";

  const kids = [
    { name: "Ori", color: "red" },
    { name: "Ron", color: "blue" },
    { name: "Sigalit", color: "green" },
    { name: "Ruti", color: "yellow" },
    { name: "Alon", color: "purple" },
  ];

  const clickHandler = () => {
    console.log("clicked");
    setBtnTitle("updated");
    // title = "updated";
  };
  return (
    <div className="mother">
      <h1>{props.title}</h1>
      <Child {...kids[0]}></Child>
      <Child {...kids[1]}></Child>
      <Child {...kids[2]}></Child>
      <Child {...kids[3]}></Child>
      <Child {...kids[4]}></Child>
      <button onClick={clickHandler}>{title}</button>
    </div>
  );
};

export default Mother;

