import React, { useState } from "react";
import getRandom1to6 from "../../utils/utils";
import CurrentPoints from "./CurrentPoints";

//!to change player, make it a boolean to switch between them more easily
// currentPlayer === null
//   ? setCurrentPlayer("player2")
//   : setCurrentPlayer(null);

function Cube({
  numberOfCubes,
  // setp1turn,
  setTotalPoints,
  setCurrentPoints,
  currentPoints,
}) {
  const [cubes, setCubes] = useState([]);
  const [p1turn, setp1turn] = useState(true);

  const rollDiceHandler = () => {
    const cubes = [];
    for (let i = 0; i < numberOfCubes; i++) {
      const dice = getRandom1to6();
      cubes.push(dice);
      setCubes((prev) => [...cubes]);
    }
    displayCubes(cubes);
  };

  const displayCubes = (arrOfPoints) => {
    const pointsPerRound = arrOfPoints.reduce((accumulator, value) => {
      return accumulator + value;
    }, 0);
    setCurrentPoints((prev) => prev + pointsPerRound);
  };

  const holdHandler = () => {
    setTotalPoints((prev) => prev + currentPoints);
    setp1turn((prev) => !prev);
  };

  return (
    <div className="flex-column">
      <div className="">
        <button id="roll-dice" onClick={rollDiceHandler}>
          roll dice
        </button>
        <button id="hold" onClick={holdHandler}>
          hold
        </button>
      </div>
      <div className="flex-row">
        {cubes.map((number) => {
          return <p>{number}</p>;
        })}
      </div>
      {/* {setp1turn((prev) => !prev)} */}
    </div>
  );
}

export default Cube;
