    player1: { name: "player1", total: 0, current: 0 },
    player2: { name: "player1", total: 0, current: 0 },
  };
  const backtochartHnadler = () => {
    SetDisplayedPage((perv) => null);
  };
  // console.log(getRandom1to6());
