    <>
      <div className="flex-row">
        <Player name={app.player1.name} />
        <button onClick={backtochartHnadler}>back to chart</button>
        <Player />
      </div>
      <div className="flex-row">
        <Cube />
        <Cube />
      </div>
    </>
  );
}

export default GamePage;
