import React, { useState } from "react";
import carMarket from "../carMarket";
import "./App.css";
import Nav from "./nav/Nav.js";
// .map((title) => {
//   console.log(title);
//   let btntitle = title[0].toUpperCase() + title.slice(1);
//   return btntitle.replace("_", " ");
// });

function App() {
  const [hero, setHero] = useState("");

  const getAllButtonsLable = (data) => {
    let res = Object.keys(data);
    return res;
  };

  const logoClickHndler = () => {
    setHero((prev) => "welcomw to our market!");
  };

  return (
    <>
      <div className="nav">
        <h2 id="logo" onClick={logoClickHndler}>
          Car market
        </h2>
        <Nav title={getAllButtonsLable(carMarket)} setHero={setHero} />
      </div>
      <div className="content-main-box">
        {/* {hero === "logo" && <HomePage />} */}
        {hero}
      </div>
    </>
  );
}

export default App;
