import React, { useState } from "react";
import getRandom1to6 from "../../utils/utils";

function Cube({ numberOfCubes }) {
  const [cubes, setCubes] = useState([]);

  const rollDiceHandler = () => {
    for (let i = 0; i < numberOfCubes; i++) {
      const cubes = [];
      const dice = getRandom1to6();
      //   console.log(dice);
      cubes.push(dice);
      setCubes(cubes);
    }
  };
