import React from "react";
import Player from "./components/Player";
// import getRandom1to6 from "../utils/utils";

import "./gamePage.css";
import Cube from "./components/Cube";

function GamePage({ SetDisplayedPage }) {
  const app = {
    player1: { name: "player 1", total: 0, current: 0 },
    player2: { name: "player 2", total: 0, current: 0 },
  };
  const backtochartHnadler = () => {
    SetDisplayedPage((perv) => null);
  };

  return (
    <>
      <div className="flex-row">
        <Player {...app.player1} />
        <button id="back-to-chart" onClick={backtochartHnadler}>
          back to chart
        </button>
        <button id="roll-dice">roll dice</button>
        <Player {...app.player2} />
      </div>
      <div className="flex-row">
        <Cube />
        <Cube />
      </div>
    </>
  );
}

export default GamePage;
