import React, { useRef } from "react";

function PlayerSelect({ names, SetDisplayedPage, setP1name, setP2name }) {
  console.log(names);
  const name1 = useRef("");
  const name2 = useRef("");

  const handlePlayButton = (e) => {
    e.preventDefault();
    console.log(name1.current.value);
    console.log(name2.current.value);
    setP1name((prev) => name1.current.value);
    names[0] = name1.current.value;
    names[1] = name2.current.value;

    SetDisplayedPage((prev) => "game-page");
  };

  return (
    <div className="game-settings-form ">
      <form className="flex-column">
        <label>player 1 name</label>
        <input ref={name1} type="text"></input>
        <label>player 2 name</label>
        <input ref={name2} type="text"></input>
        <label>choose target</label>
        <input
          type="number"
          name="target"
          id="target"
          min="100"
          max="1000"
          step="100"
        />
        <button id="play" onClick={handlePlayButton}>
          Play!
        </button>
      </form>
    </div>
  );
}

export default PlayerSelect;
