import React, { useState } from "react";

function Counter() {
  let [counter, setCounter] = useState("0");

  const clickHndler = (e) => {
    while (counter > -10 && counter < 10) {
      if (e.target.id === "plus") {
        counter++;
        setCounter(counter);
      } else {
        counter--;
        setCounter(counter);
      }
    }
    // e.target.id === "plus" ? setCounter(counter++) : setCounter(counter--);
    // console.log(e.target.id);
  };

  return (
    <div className="main-box">
      <button id="plus" onClick={clickHndler}>
        +
      </button>
      <p>{counter}</p>
      <button id="minus" onClick={clickHndler}>
        -
      </button>
    </div>
  );
}
export default Counter;
