function Chechbox(props) {
  const func = props.onChange;
  return (
    <div>
      <input type="checkbox" checked={props.checked} onChange={func(e)}></input>
      <label>{props.lable}</label>
    </div>
  );
}

function Form() {
  const checkboxHandler = (e) => {
    console.log("props", e);
    // setChecked((prevState) => !prevState);
  };

  //   let [checked, setChecked] = useState(props.checked);

  const inputs = [
    { lable: "I read the term of thr App", checked: false },
    { lable: "I accept the term of thr App", checked: false },
    { lable: "I want to get the weekly news letter", checked: true },
    { lable: "I want to go selld and offers", checked: true },
  ];
  return (
    <form>
      {inputs.map((input) => (
        <Chechbox {...input} onChange={checkboxHandler} />
      ))}
    </form>
  );
}

export default Form;
