import React, { useState } from "react";
import Child from "./Child";

const Mother = (props) => {
  useState(props.title);

  let title = props.title;

  const kids = [
    { name: "Ori", color: "red" },
    { name: "Ron", color: "blue" },
    { name: "Sigalit", color: "green" },
    { name: "Ruti", color: "yellow" },
    { name: "Alon", color: "purple" },
  ];

  const clickHandler = () => {
    console.log("clicked");
  };
  return (
    <div className="mother">
      <h1>{props.title}</h1>
      <Child {...kids[0]}></Child>
      <Child {...kids[1]}></Child>
      <Child {...kids[2]}></Child>
      <Child {...kids[3]}></Child>
      <Child {...kids[4]}></Child>
      <button onClick={clickHandler}>{title}</button>
    </div>
  );
};

export default Mother;

//! how State works
//* components are functions, keep in mind which returns jsx
//* when we are using components we are not calling them just using them like htm elements
//* react go through the whole code atrating froom the root where our app is being renderded, and display every step diving into the nested components untill there are no components left
//*to update what displays on screen we need to tell react to render those places ive made changes
//* this is why we use states
//* useState is a function provided by the react library aloows us to define vaiables as states, so chages on those vars wiil reflect on the browser because the component function will be called again
//* those functions must be called inside a component, not inside a nested function(there is one wxeption we'll talk later)
//* inside we define initial variable, which will be a refreance to changes in the values ahead in the program
